import React from 'react'
import styled from 'styled-components';
import {Link as LinkS} from 'react-scroll';




const SliderContent = ({activeIndex,imgSlider}) => {


    return (


        <>
            <div className='slide-container' >
            {
                imgSlider.map((slide, index) => {


                    const btnCarousel = styled(LinkS)` 
    text-decoration: none;
    padding : 10px 20px;
    position : absolute;
    background : blue;
    z-index : 99;
}`

                    return(            
                    <div key={index}
                    className={index === activeIndex ? "active" : "inactive"}>
                        {console.log(slide.titulo)}
                        <video src={slide.video} autoPlay loop muted/>
                        <h1>{slide.titulo}</h1>
                        <p>{slide.descrip}</p>
                        <btnCarousel to={slide.vinculo}>{slide.btnInfo}</btnCarousel>
                    </div>)
                })
            }
            </div>

        </>
    )
}



export default SliderContent
