import styled from "styled-components";

export const InfoContainer = styled.section`
    background : ${({blueBg}) => (blueBg ? 'blue' : '#f9f9f9')};
    height : 100vh;
    width : 100%;
`

export const InfoWrapper = styled.div` 
    display : grid;
    z-index : 1;
    height : 860px;
    width : 100%;
    max-width : 1100px;
    margin-right : auto;
    padding : 0 24px;
    justify-content : center;
`
