    background : #1d3557;
    display: flex;
    justify-content : center;
    align-items : center;
`

export const ServiceWrapper = styled.div`
    background : red;
    height : 400px;
`

export const TitleWrp = styled.div`
    text-align: center;

`

export const Title = styled.h1`
color : #fff;
`

export const ServicesInfo = styled.div`
    display: flex;
