import React from 'react'

const Carousel = () => {


    return (
        <>
           <CarouselContainer>
                <CarouseItems ref={listRef}>
                    {
                        data.map((item)=>{
                            return(<>
                                <Item >
                                <h1>{item.title}</h1>
                                <p>{item.descrip}</p>

                                </Item>
                            </>)
                        })
                    }
                </CarouseItems>
                <CarouselIcons>
                    <Left onClick={scrollLeft}/>
                    <Right />
                </CarouselIcons>
            </CarouselContainer>
        </>
    )
}

export default Carousel
