import React,{useState} from 'react'
import SliderContent from './SliderContent'
import Data from './data'
import Dots from './Dots';




const Carousel = (props) => {

    const [activeIndex, setActiveIndex ] = useState(0);

    const len = Data.length - 1;
    return (

        <>
            <CarouselContainer>
                <SliderContent activeIndex={activeIndex} imgSlider={Data}/>
                <Arrows prevSlide={()=> setActiveIndex(activeIndex < 1 ? len : activateIndex - 1) }
                        nextSlide={()=> setActiveIndex(activeIndex === len ? 0 : activateIndex + 1)}

                />
                <Dots activeIndex={activateIndex} imgSlider={Data} onClick={activateIndex=> setActiveIndex }/>
            </CarouselContainer>
        </>
    )
}

export default Carousel
