import React,{useState,useEffect} from 'react'
import axios from 'axios'
import {
    FormContainer,
    FormWrap,
    FormDisplay,
    FormItems,
    FormLabel,
    FormInput,
    FormTextArea,
    FormSubmit
} from './formElements'




const Form = () => {

    const [datos, setDatos] = useState({
        name : "",
        email : "",
        phone : "",
        affair : "",
        message : ""
    });

    const [send,setSend] = useState(false);

    const handleSend = ()=>{
        setSend(true);
        const api = axios.create({
            baseURL : 'http://localhost:3001/send-email'
        });
        api.post('/',{...datos});

    }

    useEffect(() => {
        handleSend();
    },[]);

    return (
        <>
            <FormContainer id='contact'>
                <FormWrap>
                    <FormDisplay id="form--contact" onSubmit={handleSend}>
                        <FormItems>
                            <FormLabel for='name' id='lbName'>Nombre</FormLabel>
                            <FormInput id='name' value={datos.name} onChange={(e)=>setDatos(e.name.value)} placeholder='ingrese su nombre' required></FormInput>
                        </FormItems>
                        <FormItems>
                            <FormLabel for='mail' id='lbName'>Correo</FormLabel>
                            <FormInput id='mail' value={datos.email} type="email" name='mail' placeholder='ingrese su correo' required></FormInput>
                        </FormItems>
                        <FormItems>
                            <FormLabel for='number' id='lbName'>Numero </FormLabel>
                            <FormInput id='number' value={datos.number} name='number' placeholder='ingrese su numero' required></FormInput>
                        </FormItems>
                        <FormItems>
                            <FormLabel for='affair' id='lbName'>Asunto</FormLabel>
                            <FormInput id='affair' value={datos.affair} name='affair' placeholder='ingrese asunto' required></FormInput>
                        </FormItems>
                        <FormItems>
                            <FormLabel for='message' id='lbName'>mensaje</FormLabel>
                            <FormTextArea id='message' value={datos.message} name='message' placeholder='ingrese su mensaje' required></FormTextArea>
                        </FormItems>
                        <FormItems>
                            <FormSubmit id='submit' type='submit'>Enviar</FormSubmit>
                        </FormItems>
                    </FormDisplay>
                </FormWrap>
            </FormContainer>
        </>
    )
}

export default Form
