import React,{ useRef } from 'react'
import { 
    CarouselContainer,
    CarouseItems,
    Item,
    CarouselIcons,
    Left,
    Right
}from './CarouselElements'
const Carousel = ({data}) => {
    const listRef = useRef(null);

    const scrollLeft = () => {
        if (listRef.current) {
          listRef.current.scrollBy({
            top: 0,
            left: 100,
            behavior: "smooth",
          });
        }
      };
    return (
        <>
           <CarouselContainer>
                <CarouseItems ref={listRef}>
                    {
                        data.map((item)=>{
                            return(<>
                                <Item >
                                <h1>{item.title}</h1>
                                <p>{item.descrip}</p>

                                </Item>
                            </>)
                        })
                    }
                </CarouseItems>
                <CarouselIcons>
                    <Left onClick={scrollLeft}/>
                    <Right />
                </CarouselIcons>
            </CarouselContainer>
        </>
    )
}

export default Carousel
