import React,{useState} from 'react'
import SliderContent from './SliderContent'
import imgSlide from './data'
import Arrows from './Arrows';
import { CarouselContainer } from './CarouselElements';



const Carousel = () => {

    const [activeIndex, setActiveIndex ] = useState(0);

    const len = imgSlide.length - 1;

    return (

        <>
            <CarouselContainer>
                <SliderContent activeIndex={activeIndex} imgSlider={imgSlide}/>
                <Arrows prevSlide={()=>{setActiveIndex(activeIndex < 1 ? len : activeIndex - 1)} }
                        nextSlide={()=>setActiveIndex(activeIndex === len ? 0 : activeIndex + 1)}
                />
            </CarouselContainer>
        </>
    )
}

export default Carousel
